
public class Calculator {
	
	public Calculator() {
	}
	
	public static int add(int x, int y) {
		return x+y;
		
	}

	public static int sub(int x, int y) {
		// TODO Auto-generated method stub
		return x-y;
	}

	public static int multi(int x, int y) {
		// TODO Auto-generated method stub
		return x*y;
	}

	public static int div(int x, int y) {
		// TODO Auto-generated method stub
		return x/y;
	}

}
